import React, { Component } from "react";
import axios from 'axios';
import PaginatedResult from './PaginatedResult';
class FetchUser extends Component {
    state = {
        users: [],
        totalRecords:0,
        limit: 6
    }
    componentDidMount(){
        this.loadData(1);
    }
    loadData = (page) =>{
        console.log('page>>',page)
        axios
            .get(`https://localhost:8080/api/users/`+page)
            .then(res => {

                const user = res.data;
                console.log('data>>>>',user)
                this.setState({
                    user: user.data,
                    totalRecords : user.total ? user.total : 0,
                    limit : user.per_page ? user.per_page : 6
                })
            });
    }
    getPaginatedData = page =>{
        this.loadData(page);
    }
    render(){
        const { data, totalRecords, limit } = this.state;
        return(
            <div>
                <table className="table">
                    <thead>
                        <tr>
                            <th className="align-middle bt-0">Id</th>
                            <th className="align-middle bt-0">Uname</th>
                            <th className="align-middle bt-0">Department</th>
                            <th className="align-middle bt-0">Email</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            data && data.length > 0 ?
                            data.map((item,index)=>(
                                <tr>
                                    <td scope="col">{item.id}</td>
                                    <td scope="col">item.uname</td>
                                    <td scope="col">{item.department}</td>
                                    <td scope="col">{item.email}</td>
                                </tr>
                            )) :
                            <h4>No Data Found!!</h4>
                        }
                    </tbody>
                </table>
                {totalRecords > 6 &&
                    <PaginationComponent
                        getUsers={this.getPaginatedData} 
                        totalRecords={totalRecords}
                        itemsCountPerPage = {limit} />
                }
            </div>
        );
    }
}

export default FetchUser;
